import React from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Spinner from 'react-bootstrap/Spinner'
import 'bootstrap/dist/css/bootstrap.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: '', answer: '', loading: false };
  }

  handleSubmit = async (event) => {
    event.preventDefault();
    this.setState({ loading: true })
    console.log('submitted')
    let answer = await get_answer(this.state.value)
    this.setState({ loading: false })
    this.setState({ answer: answer })
  }

  handleChange = (event) => {
    this.setState({ value: event.target.value });
  }
  render = () => {
    return (
      <Container className="App" style={{ display: "flex", justifyContent: "center" }}>
        <Col md={8} lg={8}>
          <h1 style={{ marginBottom: "1em", marginTop: "0.3em" }}>Youtube Comments Sentiment Emotion</h1>
          <Form onSubmit={this.handleSubmit} className="form">
            <Form.Group>
              <Form.Control onChange={this.handleChange} type="search" placeholder="Ask anything about COVID-19 here" />
            </Form.Group>
            <Button type="submit" style={{ marginBottom: "1em" }} variant="primary" type="submit">
              {this.state.loading ? <div><span>Searching... </span> <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
              /></div> : <span>Search</span>}
            </Button>
          </Form>
          <div style={{ textAlign: "justify" }}>
            {this.state.loading ? undefined : this.state.answer}
          </div>
        </Col>
      </Container>
    )
  }
}

export default App;
